<?php\n// Load the HTML content\n$html = '<!DOCTYPE html>\n<html>\n<body>\n    <div id="remove-me">This will be removed</div>\n    <div id="keep-me">This will stay</div>\n</body>\n</html>';\n\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // Suppress warnings for invalid HTML\n$dom->loadHTML($html);\nlibxml_clear_errors();\n\n// Find the element to remove\n$element = $dom->getElementById('remove-me');\n\nif ($element) {\n    // Remove the element\n    $element->parentNode->removeChild($element);\n}\n\n// Save the updated HTML\necho $dom->saveHTML();\n?>