// Pseudo-code for formatting the output in PHP\n$results = []; // Assume this is the array of results fetched from the database\n\nforeach ($results as $row) {\n    echo $row['category'] . "\n";\n    if (!empty($row['subcategory'])) {\n        echo $row['category'] . ' > ' . $row['subcategory'] . "\n";\n    }\n    if (!empty($row['subsubcategory'])) {\n        echo $row['subsubcategory'] . "\n";\n    }\n}