class Cart extends CI_Controller {\n\n    public function __construct() {\n        parent::__construct();\n        $this->load->model('Cart_model');\n    }\n\n    public function add() {\n        $product_id = $this->input->post('product_id');\n        $quantity = $this->input->post('quantity');\n\n        $session_id = $this->session->session_id;\n\n        $this->Cart_model->add_to_cart($session_id, $product_id, $quantity);\n        // Redirect or load view\n    }\n\n    public function update() {\n        $cart_item_id = $this->input->post('cart_item_id');\n        $quantity = $this->input->post('quantity');\n\n        $this->Cart_model->update_cart_item($cart_item_id, $quantity);\n        // Redirect or load view\n    }\n\n    public function remove() {\n        $cart_item_id = $this->input->post('cart_item_id');\n\n        $this->Cart_model->remove_cart_item($cart_item_id);\n        // Redirect or load view\n    }\n\n    public function view() {\n        $session_id = $this->session->session_id;\n\n        $data['cart_items'] = $this->Cart_model->get_cart_items($session_id);\n        $this->load->view('cart_view', $data);\n    }\n}