function linearRegression($points) {\n    $n = count($points);\n    if ($n === 0) {\n        throw new InvalidArgumentException("At least one point is required");\n    }\n\n    $sumX = 0;\n    $sumY = 0;\n    $sumXY = 0;\n    $sumX2 = 0;\n\n    foreach ($points as $point) {\n        $x = $point[0];\n        $y = $point[1];\n        $sumX += $x;\n        $sumY += $y;\n        $sumXY += $x * $y;\n        $sumX2 += $x * $x;\n    }\n\n    $m = ($n * $sumXY - $sumX * $sumY) / ($n * $sumX2 - $sumX * $sumX);\n    $b = ($sumY - $m * $sumX) / $n;\n\n    return ['m' => $m, 'b' => $b];\n}\n\n// Example usage:\n$points = [[1, 2], [2, 3], [4, 5], [3, 4]];\n$line = linearRegression($points);\necho "Slope (m): " . $line['m'] . "\n";\necho "Intercept (b): " . $line['b'] . "\n";