class Cart_model extends CI_Model {\n\n    public function add_to_cart($session_id, $product_id, $quantity) {\n        $data = array(\n            'session_id' => $session_id,\n            'product_id' => $product_id,\n            'quantity' => $quantity\n        );\n        $this->db->insert('shopping_cart', $data);\n    }\n\n    public function update_cart_item($cart_item_id, $quantity) {\n        $this->db->where('id', $cart_item_id);\n        $this->db->update('shopping_cart', array('quantity' => $quantity));\n    }\n\n    public function remove_cart_item($cart_item_id) {\n        $this->db->where('id', $cart_item_id);\n        $this->db->delete('shopping_cart');\n    }\n\n    public function get_cart_items($session_id) {\n        $this->db->where('session_id', $session_id);\n        $query = $this->db->get('shopping_cart');\n        return $query->result_array();\n    }\n}