<?php\n\n$ra_category_id = 3; \n$ra_category = $wpdb->get_results("SELECT name FROM $wpdb->terms WHERE term_id = $ra_category_id");\n\n// Check if the result is not empty\nif (!empty($ra_category)) {\n    // Access the first element in the array\n    $ra_category_name = $ra_category[0]->name;\n} else {\n    $ra_category_name = "Category not found";\n}\n\n?>\n\n<h3>Category: <?php echo esc_html($ra_category_name); ?></h3>