<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $con = mysqli_connect("localhost", "146687", "password", "database_name");\n    if (!$con) {\n        die('Could not connect: ' . mysqli_connect_error());\n    }\n\n    $user = 'username'; // You should get the username from the session or another secure source\n    $item1 = mysqli_real_escape_string($con, $_POST['item1']);\n    $item2 = mysqli_real_escape_string($con, $_POST['item2']);\n    $item3 = mysqli_real_escape_string($con, $_POST['item3']);\n    $item4 = mysqli_real_escape_string($con, $_POST['item4']);\n\n    $sql = "INSERT INTO orders (user, item1, item2, item3, item4) VALUES ('$user', '$item1', '$item2', '$item3', '$item4')";\n\n    if (mysqli_query($con, $sql)) {\n        echo "Your order has been added";\n    } else {\n        echo "There was an error adding your order to the database: " . mysqli_error($con);\n    }\n\n    mysqli_close($con);\n} else {\n    echo "Invalid request method.";\n}\n?>