<?php\n$host = '127.0.0.1';\n$db = 'your_database';\n$user = 'your_username';\n$pass = 'your_password';\n$charset = 'utf8mb4';\n\n$dsn = "mysql:host=$host;dbname=$db;charset=$charset";\n$options = [\n    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES   => false,\n];\n\ntry {\n    $pdo = new PDO($dsn, $user, $pass, $options);\n} catch (\PDOException $e) {\n    throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n// Create\nfunction create($pdo, $data) {\n    $sql = "INSERT INTO your_table (column1, column2) VALUES (?, ?)";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([$data['column1'], $data['column2']]);\n}\n\n// Read\nfunction read($pdo, $id) {\n    $sql = "SELECT * FROM your_table WHERE id = ?";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([$id]);\n    return $stmt->fetch();\n}\n\n// Update\nfunction update($pdo, $id, $data) {\n    $sql = "UPDATE your_table SET column1 = ?, column2 = ? WHERE id = ?";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([$data['column1'], $data['column2'], $id]);\n}\n\n// Delete\nfunction delete($pdo, $id) {\n    $sql = "DELETE FROM your_table WHERE id = ?";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([$id]);\n}\n?>