<?php\n// Set the maximum execution time to unlimited\nset_time_limit(0);\n\n// Increase MySQL timeout settings\n$db->query("SET SESSION wait_timeout = 600"); // 10 minutes\n$db->query("SET SESSION interactive_timeout = 600"); // 10 minutes\n\n// Prepare your query\n$query = "SLOW QUERY";\n\n// Execute the query\nif (!$db->real_query($query)) {\n    echo "error: " . $db->error;\n} else {\n    // Use async query if needed\n    $result = $db->use_result();\n    while ($row = $result->fetch_assoc()) {\n        // Process each row if needed\n    }\n    $result->free();\n}\n\n// Close the database connection\n$db->close();\n?>