<?php\n$input_url = '/users/admin/steve/';\n$input_url_escaped = $mysqli->real_escape_string($input_url);\n\n// Step 1: Check for an exact match\n$query = "SELECT id, url FROM urls WHERE url = '$input_url_escaped' LIMIT 1";\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    // Exact match found\n    $row = $result->fetch_assoc();\n    echo 'Exact match found: ' . $row['id'];\n} else {\n    // Step 2: Check for wildcard matches\n    $query = "SELECT id, url FROM urls WHERE url LIKE '%*' ORDER BY LENGTH(url) DESC";\n    $result = $mysqli->query($query);\n\n    $found = false;\n    while ($row = $result->fetch_assoc()) {\n        // Remove the wildcard character for comparison\n        $wildcard_url = rtrim($row['url'], '*');\n        \n        if (strpos($input_url, $wildcard_url) === 0) {\n            // Match found\n            echo 'Wildcard match found: ' . $row['id'];\n            $found = true;\n            break;\n        }\n    }\n\n    if (!$found) {\n        echo 'No match found';\n    }\n}\n?>