$cache = []; // Simple in-memory cache for example purposes\n\nfunction avatarExists($username) {\n    global $cache;\n\n    if (isset($cache[$username])) {\n        return $cache[$username];\n    }\n\n    $avatarPath = '/path/to/avatars/' . $username . '.png';\n    $exists = file_exists($avatarPath);\n\n    // Cache the result\n    $cache[$username] = $exists;\n\n    return $exists;\n}\n\nfunction getAvatarUrl($username) {\n    if (avatarExists($username)) {\n        return '/url/to/avatars/' . $username . '.png';\n    } else {\n        return '/url/to/default/avatar.png';\n    }\n}\n\n// Example usage\n$username = 'john_doe';\n$avatarUrl = getAvatarUrl($username);\necho '<img src="' . $avatarUrl . '" alt="User Avatar">';