function getAvatarUrl($username, $pdo) {\n    $stmt = $pdo->prepare("SELECT avatar_filename FROM users WHERE username = :username");\n    $stmt->execute(['username' => $username]);\n    $avatar = $stmt->fetchColumn();\n\n    if ($avatar) {\n        return '/url/to/avatars/' . $avatar;\n    } else {\n        return '/url/to/default/avatar.png';\n    }\n}\n\n// Example usage\n$username = 'john_doe';\n$pdo = new PDO('mysql:host=hostname;dbname=database', 'username', 'password');\n$avatarUrl = getAvatarUrl($username, $pdo);\necho '<img src="' . $avatarUrl . '" alt="User Avatar">';