<?php\n$chars = array("a", "b", "c");\n$string = "log dog hat bat";\n\n// Convert the array of forbidden characters into a string\n$forbidden_chars = implode('', $chars);\n\n// Find the position of the first occurrence of any forbidden character\n$pos = strcspn($string, $forbidden_chars);\n\n// Extract the substring before the first forbidden character\n$result = substr($string, 0, $pos);\n\necho $result;  // Output: log dog h\n?>