/**\n * Returns a template after replacing variables\n * @param   string  $template   Template contents\n * @param   array   $vars       Associative array of variables\n * @return  string  Template output\n */\nfunction output_template($template, $vars) {\n    foreach ($vars as $key => $value) {\n        $template = str_replace('{$' . $key . '}', $value, $template);\n    }\n    return $template;\n}\n\n// Example usage:\n$name = 'Ross';\n$template = '<p>Hello, my name is {$name}.</p>';\n$vars = ['name' => $name];\n\necho output_template($template, $vars);