<?php\nfunction getClientLocale() {\n    // Check if the HTTP_ACCEPT_LANGUAGE header is set\n    if (isset($_SERVER['HTTP_ACCEPT_LANGUAGE'])) {\n        // Extract the locale from the header\n        $locale = locale_accept_from_http($_SERVER['HTTP_ACCEPT_LANGUAGE']);\n        return $locale;\n    }\n    return 'en'; // Default to English if no locale information is available\n}\n\n$clientLocale = getClientLocale();\necho "Client locale: " . $clientLocale;\n?>