// Register Custom Post Types in your theme's functions.php\nfunction create_custom_post_types() {\n    // Register Custom Post Type for Banners\n    register_post_type('banner',\n        array(\n            'labels'      => array(\n                'name'          => __('Banners'),\n                'singular_name' => __('Banner'),\n            ),\n            'public'      => true,\n            'has_archive' => true,\n            'rewrite'     => array('slug' => 'banners'),\n        )\n    );\n\n    // Register Custom Post Type for Photo Galleries\n    register_post_type('photo_gallery',\n        array(\n            'labels'      => array(\n                'name'          => __('Photo Galleries'),\n                'singular_name' => __('Photo Gallery'),\n            ),\n            'public'      => true,\n            'has_archive' => true,\n            'rewrite'     => array('slug' => 'photo-galleries'),\n        )\n    );\n}\nadd_action('init', 'create_custom_post_types');