<?php\n   // Gmail server settings\n   $hostname = '{pop.gmail.com:995/pop3/ssl}INBOX';\n   $username = 'your-email@gmail.com';\n   $password = 'your-email-password';\n   \n   // Connect to the mailbox\n   $inbox = imap_open($hostname, $username, $password) or die('Cannot connect to Gmail: ' . imap_last_error());\n   \n   // Get the number of emails in the mailbox\n   $emails = imap_search($inbox, 'ALL');\n   \n   // Check if emails are found\n   if ($emails) {\n       // Sort emails by date\n       rsort($emails);\n       \n       // Loop through each email\n       foreach ($emails as $email_number) {\n           // Get the email overview\n           $overview = imap_fetch_overview($inbox, $email_number, 0);\n           $message = imap_fetchbody($inbox, $email_number, 2);\n           \n           // Output the email details\n           echo "Subject: " . $overview[0]->subject . "<br>";\n           echo "From: " . $overview[0]->from . "<br>";\n           echo "Date: " . $overview[0]->date . "<br>";\n           echo "Message: " . $message . "<br><hr>";\n       }\n   } else {\n       echo "No emails found.";\n   }\n   \n   // Close the connection\n   imap_close($inbox);\n   ?>