$cookie_name = "Name";\n$cookie_value = $cookie;\n$cookie_lifetime = time() + (60 * 24 * 365); // 1 year\n$cookie_path = "/"; // Available in all directories\n$cookie_domain = $_SERVER['HTTP_HOST']; // Set cookie for the current domain\n$cookie_secure = isset($_SERVER['HTTPS']); // Set to true if HTTPS\n$cookie_httponly = true; // Cookie accessible only through HTTP\n$cookie_samesite = 'None'; // Allow cross-site usage, especially in iframes\n\nsetcookie($cookie_name, $cookie_value, [\n    'expires' => $cookie_lifetime,\n    'path' => $cookie_path,\n    'domain' => $cookie_domain,\n    'secure' => $cookie_secure,\n    'httponly' => $cookie_httponly,\n    'samesite' => $cookie_samesite,\n]);