<?php\nfunction isPronounceable($word) {\n    // Define vowels\n    $vowels = ['a', 'e', 'i', 'o', 'u'];\n    \n    // Convert the word to lowercase\n    $word = strtolower($word);\n    \n    // Initialize last character type\n    $lastCharType = null; // null, 'vowel', or 'consonant'\n    \n    // Check alternating pattern\n    $alternating = true;\n    \n    for ($i = 0; $i < strlen($word); $i++) {\n        $char = $word[$i];\n        \n        if (in_array($char, $vowels)) {\n            if ($lastCharType === 'vowel') {\n                $alternating = false;\n                break;\n            }\n            $lastCharType = 'vowel';\n        } else {\n            if ($lastCharType === 'consonant') {\n                $alternating = false;\n                break;\n            }\n            $lastCharType = 'consonant';\n        }\n    }\n    \n    return $alternating;\n}\n\n// Test cases\n$words = ['namelet', 'nameoic', 'example', 'pronounce', 'php'];\n\nforeach ($words as $word) {\n    echo $word . ": " . (isPronounceable($word) ? 'Pronounceable' : 'Not Pronounceable') . "\n";\n}\n?>