<?php\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpPresentation\IOFactory;\nuse PhpOffice\PhpSpreadsheet\Spreadsheet;\nuse PhpOffice\PhpSpreadsheet\Writer\Xlsx;\n\n// Load the PowerPoint file\n$pptFile = 'your_presentation.pptx';\n$objPHPPowerPoint = IOFactory::load($pptFile);\n\n// Create a new Spreadsheet object\n$spreadsheet = new Spreadsheet();\n$sheet = $spreadsheet->getActiveSheet();\n\n// Initialize row counter for Excel\n$row = 1;\n\n// Loop through slides\nforeach ($objPHPPowerPoint->getAllSlides() as $slideIndex => $slide) {\n    // Loop through all shapes in the slide\n    foreach ($slide->getShapeCollection() as $shape) {\n        if ($shape instanceof \PhpOffice\PhpPresentation\Shape\RichText) {\n            // Loop through each paragraph in the shape\n            foreach ($shape->getParagraphs() as $paragraph) {\n                // Check if the paragraph is a bullet point\n                if ($paragraph->isBullet()) {\n                    $bulletText = $paragraph->getText();\n                    // Insert bullet text and slide number into Excel\n                    $sheet->setCellValue("A{$row}", $bulletText);\n                    $sheet->setCellValue("B{$row}", $slideIndex + 1);\n                    $row++;\n                }\n            }\n        }\n    }\n}\n\n// Write the Excel file\n$writer = new Xlsx($spreadsheet);\n$excelFile = 'bullets.xlsx';\n$writer->save($excelFile);\n\necho "Bullet points have been successfully extracted to {$excelFile}.\n";\n?>