session_start();\n\n$content = 'Your content here';\n\n// Set a session variable if it's an AJAX request\nif (!empty($_SERVER['HTTP_X_REQUESTED_WITH']) && strtolower($_SERVER['HTTP_X_REQUESTED_WITH']) == 'xmlhttprequest') {\n    $_SESSION['is_ajax'] = true;\n} else {\n    $_SESSION['is_ajax'] = false;\n}\n\n// Check the session variable to decide the response\nif (isset($_SESSION['is_ajax']) && $_SESSION['is_ajax'] == true) {\n    // It's an AJAX request\n    echo $content;\n} else {\n    // It's not an AJAX request\n    include_once 'about-header.php';\n    echo $content;\n    include_once 'about-footer.php';\n}