<?php\n   require 'vendor/autoload.php';\n\n   use Fabiang\Xmpp\Client;\n   use Fabiang\Xmpp\Options;\n   use Fabiang\Xmpp\Protocol\Message;\n\n   // Configure the XMPP client options\n   $options = new Options('tcp://talk.google.com:5222');\n   $options->setUsername('your-email@gmail.com')\n           ->setPassword('your-password')\n           ->setTo('gmail.com');\n\n   // Create the client\n   $client = new Client($options);\n   $client->connect();\n\n   // Add event listener for incoming messages\n   $client->getEventManager()->attach('message', function ($event) use ($client) {\n       $message = $event->getMessage();\n       $from = $message->getFrom();\n       $body = $message->getBody();\n\n       // Example: Simple autoresponder\n       if ($body) {\n           $reply = new Message();\n           $reply->setMessage('I am currently away. I will get back to you soon.')\n                 ->setTo($from);\n\n           $client->send($reply);\n       }\n   });\n\n   // Listen for incoming messages\n   $client->send(new Fabiang\Xmpp\Protocol\Presence);\n   while (true) {\n       $client->receive();\n   }\n\n   $client->disconnect();\n   ?>