// Assuming you are using MySQLi for database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the statement\n$stmt = $mysqli->prepare("\n    SELECT\n        ss.*,\n        se.name as engine,\n        ss.last_run_at + INTERVAL ss.refresh_frequency DAY as next_run_at,\n        se.logo_name\n    FROM \n        searches ss \n    JOIN \n        search_engines se ON ss.engine_id = se.id\n    WHERE\n        ss.user_id = ?\n    GROUP BY \n        ss.id\n    ORDER BY \n        ss.project_id, ss.domain, ss.keywords\n");\n\n// Bind the $user_id parameter to the statement\n$stmt->bind_param("i", $user_id);\n\n// Set the $user_id variable\n$user_id = $_GET['user_id']; // Or wherever you get the user_id from\n\n// Execute the statement\n$stmt->execute();\n\n// Get the result\n$result = $stmt->get_result();\n\n// Fetch data\nwhile ($row = $result->fetch_assoc()) {\n    // Process each row\n}\n\n// Close the statement and connection\n$stmt->close();\n$mysqli->close();