<?php\n// Database connection\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Set charset to UTF-8\nif (!$mysqli->set_charset("utf8")) {\n    printf("Error loading character set utf8: %s\n", $mysqli->error);\n    exit();\n}\n\n// Set content type to UTF-8\nheader('Content-Type: text/html; charset=utf-8');\n\n// Fetching data from the database\n$result = $mysqli->query("SELECT ARTICLE_DESC FROM articles WHERE ARTICLE_NO = '220288560247'");\n$row = $result->fetch_assoc();\n\n// Displaying the HTML content\necho htmlspecialchars($row['ARTICLE_DESC'], ENT_QUOTES, 'UTF-8');\n\n// Close connection\n$mysqli->close();\n?>