$str_template = "<html><head><title>{the_title}</title></head><body>{the_content}</body></html>";\n\n$some_runtime_generated_title = "My Page Title";\n$some_runtime_generated_content = "Here is the page content.";\n\n$str_template = str_replace('{the_title}', $some_runtime_generated_title, $str_template);\n$str_template = str_replace('{the_content}', $some_runtime_generated_content, $str_template);\n\necho $str_template;