<?php\nfunction getBookTitleFromISBN($isbn) {\n    $apiUrl = "https://www.googleapis.com/books/v1/volumes?q=isbn:" . $isbn;\n    \n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $apiUrl);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    $response = curl_exec($ch);\n    curl_close($ch);\n    \n    $data = json_decode($response, true);\n    \n    if (isset($data['items'][0]['volumeInfo']['title'])) {\n        return $data['items'][0]['volumeInfo']['title'];\n    } else {\n        return "Title not found";\n    }\n}\n\n// Example usage\n$isbn = "9780143127741";\n$title = getBookTitleFromISBN($isbn);\necho "The title of the book with ISBN $isbn is: $title";\n?>