<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare and execute SQL statement\n$stmt = $conn->prepare("SELECT php_code FROM your_table WHERE id = ?");\n$stmt->bind_param("i", $id);\n$id = 1; // Example ID\n$stmt->execute();\n$stmt->bind_result($php_code);\n$stmt->fetch();\n$stmt->close();\n$conn->close();\n?>