<?php\n// Assuming $php_code is retrieved and sanitized\nif (!empty($php_code)) {\n    try {\n        // Use output buffering to capture the evaluated code's output\n        ob_start();\n        eval('?>' . $php_code);\n        $output = ob_get_clean();\n        echo $output;\n    } catch (ParseError $e) {\n        // Handle parse errors gracefully\n        echo "An error occurred while executing the PHP code.";\n    }\n} else {\n    echo "No PHP code found.";\n}\n?>