<?php\nfunction cachePage($fileName, $content) {\n    $cacheFile = 'cache/' . md5($fileName) . '.html';\n    file_put_contents($cacheFile, $content);\n}\n\nfunction getCachedPage($fileName) {\n    $cacheFile = 'cache/' . md5($fileName) . '.html';\n    if (file_exists($cacheFile) && (time() - filemtime($cacheFile)) < 3600) { // Cache for 1 hour\n        return file_get_contents($cacheFile);\n    }\n    return false;\n}\n\n// Usage\n$pageId = 'home';\n$cachedContent = getCachedPage($pageId);\nif ($cachedContent) {\n    echo $cachedContent;\n} else {\n    ob_start();\n    // Render your PHP template here\n    echo "<h1>Welcome to My Site</h1>";\n    $content = ob_get_clean();\n    cachePage($pageId, $content);\n    echo $content;\n}\n?>