<?php\nfunction timeAgo($datetime, $full = false) {\n    $now = new DateTime;\n    $ago = new DateTime($datetime);\n    $diff = $now->diff($ago);\n\n    $diff->w = floor($diff->d / 7);\n    $diff->d -= $diff->w * 7;\n\n    $string = array(\n        'y' => 'year',\n        'm' => 'month',\n        'w' => 'week',\n        'd' => 'day',\n        'h' => 'hour',\n        'i' => 'minute',\n        's' => 'second',\n    );\n    foreach ($string as $k => &$v) {\n        if ($diff->$k) {\n            $v = $diff->$k . ' ' . $v . ($diff->$k > 1 ? 's' : '');\n        } else {\n            unset($string[$k]);\n        }\n    }\n\n    if (!$full) $string = array_slice($string, 0, 1);\n    return $string ? implode(', ', $string) . ' ago' : 'just now';\n}\n\n// Example usage\necho timeAgo('2023-10-10 10:00:00');  // Replace with your datetime\n?>