<?php\n// Assuming $fb_uids is the array of Facebook user IDs\n$fb_uids = [/* your array of fb_uids */];\n\n// Convert the array to a comma-separated string\n$fb_uids_str = implode(',', array_map('intval', $fb_uids));\n\n// Create the query using the IN operator\n$query = "SELECT DISTINCT(username) FROM users WHERE fb_uid IN ($fb_uids_str)";\n\n// Execute the query\n$result = mysqli_query($connection, $query);\n\nif ($result) {\n    while ($row = mysqli_fetch_assoc($result)) {\n        echo $row['username'] . "\n";\n    }\n} else {\n    echo "Error: " . mysqli_error($connection);\n}\n?>