class Some_model extends CI_Model {\n\n    protected $auth_model;\n\n    public function __construct() {\n        parent::__construct();\n        // Get the main CodeIgniter object\n        $CI =& get_instance();\n        // Load the Auth_model\n        $CI->load->model('Auth_model');\n        // Assign the loaded model to a property\n        $this->auth_model = $CI->Auth_model;\n    }\n\n    public function some_method() {\n        // Now you can access methods from Auth_model\n        $is_logged_in = $this->auth_model->check_login();\n        // Do something with the result\n    }\n}