// Check if the array is already cached\nif (apcu_exists('names_array')) {\n    $names = apcu_fetch('names_array');\n} else {\n    // Create and sort the array\n    $names = array("Anna", "Jackson", "Owen", "..." /* other names */);\n    sort($names);\n    // Store the array in cache\n    apcu_store('names_array', $names);\n}\n\n// Now you can proceed with the rest of your logic\n$input = $_GET['input']; // Assuming the input value is sent via GET\n$filtered_names = array_filter($names, function($name) use ($input) {\n    return stripos($name, $input) !== false;\n});\n\n// Return the filtered names as a JSON response\nheader('Content-Type: application/json');\necho json_encode(array_values($filtered_names));