<?php\nfunction getFavicon($domain) {\n    // Common favicon locations\n    $common_locations = [\n        "https://$domain/favicon.ico",\n        "https://$domain/favicon.png",\n        "https://$domain/favicon.jpg"\n    ];\n\n    // Try to get favicon from common locations\n    foreach ($common_locations as $url) {\n        if (@get_headers($url)[0] !== 'HTTP/1.1 404 Not Found') {\n            return $url;\n        }\n    }\n\n    // Fallback: Parse HTML to find favicon link (requires file_get_contents)\n    $html = @file_get_contents("https://$domain");\n    if ($html === FALSE) {\n        return null;\n    }\n\n    // Use regex to find favicon link\n    if (preg_match('/<link.*?rel=["\'].*?icon["\'].*?href=["\'](.*?)["\'].*?>/i', $html, $matches)) {\n        $favicon_url = $matches[1];\n        if (parse_url($favicon_url, PHP_URL_SCHEME) === null) {\n            $favicon_url = "https://$domain" . $favicon_url;\n        }\n        return $favicon_url;\n    }\n\n    return null;\n}\n\nfunction saveFaviconAsPng($url, $savePath) {\n    $image_data = @file_get_contents($url);\n    if ($image_data === FALSE) {\n        return false;\n    }\n\n    $image = imagecreatefromstring($image_data);\n    if ($image === FALSE) {\n        return false;\n    }\n\n    imagepng($image, $savePath);\n    imagedestroy($image);\n    return true;\n}\n\n$domain = "example.domain.tld";\n$favicon_url = getFavicon($domain);\nif ($favicon_url) {\n    $savePath = __DIR__ . "/favicons/$domain.png";\n    if (saveFaviconAsPng($favicon_url, $savePath)) {\n        echo "Favicon saved successfully!";\n    } else {\n        echo "Failed to save favicon.";\n    }\n} else {\n    echo "No favicon found.";\n}\n?>