<?php\nfunction remove_line_breaks_between_ul($content) {\n    return preg_replace_callback(\n        '/<ul>(.*?)<\/ul>/s',\n        function ($matches) {\n            return '<ul>' . preg_replace('/\s*\n\s*/', '', $matches[1]) . '</ul>';\n        },\n        $content\n    );\n}\n\n$input_html = '<ul>\n<li>item one</li>\n<li>item two</li>\n</ul>';\n\n$output_html = remove_line_breaks_between_ul($input_html);\necho $output_html;\n?>