$usrID = 25;\n$string = "22,23,24,25";\n\n// Use a regular expression to match the usrID with or without a trailing comma\n$pattern = '/\b' . preg_quote($usrID, '/') . '\b,?/';\n$receivers = preg_replace($pattern, '', $string);\n\n// Remove any leading or trailing commas that may result from removing the usrID\n$receivers = trim($receivers, ',');\n\necho $receivers;  // Outputs "22,23,24"