<?php\nfunction getFieldContent($submission_info, $field_name) {\n    foreach ($submission_info as $field) {\n        if (isset($field['field_name']) && $field['field_name'] === $field_name) {\n            return $field['content'];\n        }\n    }\n    return null; // Return null if the field name is not found\n}\n\n// Example usage\n$submission_info = [\n    // Your array data here\n];\n\n$field_name = 'loanOfficer';\n$content = getFieldContent($submission_info, $field_name);\n\nif ($content !== null) {\n    echo "The content for $field_name is: $content";\n} else {\n    echo "Field name $field_name not found.";\n}\n?>