session_start();\n\n// Other code here\n\n// Display the contents of $_REQUEST\nvar_dump($_REQUEST);\n\n// Check each superglobal to determine the source of each variable\nforeach ($_REQUEST as $key => $value) {\n    if (isset($_GET[$key])) {\n        echo "$key is from GET\n";\n    } elseif (isset($_POST[$key])) {\n        echo "$key is from POST\n";\n    } elseif (isset($_COOKIE[$key])) {\n        echo "$key is from COOKIE\n";\n    } else {\n        echo "$key is from an unknown source\n";\n    }\n}