<?php\n    require 'vendor/autoload.php';\n\n    use PhpMimeMailParser\Parser;\n\n    // Assume $emailString contains your email content as a string\n    $emailString = "Your raw email string here";\n\n    // Initialize the parser and set the email string\n    $parser = new Parser();\n    $parser->setText($emailString);\n\n    // Get all attachments\n    $attachments = $parser->getAttachments();\n\n    foreach ($attachments as $attachment) {\n        // Get the file name and content of the attachment\n        $filename = $attachment->getFilename();\n        $content = $attachment->getContent();\n\n        // Save the attachment to a file\n        file_put_contents('/path/to/save/' . $filename, $content);\n    }\n\n    echo "Attachments have been extracted and saved.";\n    ?>