function getMimeType($file) {\n    // Ensure the file exists before attempting to get its MIME type\n    if (!file_exists($file)) {\n        return false;\n    }\n\n    // Use the 'file' command to get the MIME type\n    $output = [];\n    $returnCode = 0;\n\n    // Execute the command and capture the output\n    exec("file --mime-type -b " . escapeshellarg($file), $output, $returnCode);\n\n    // Check if the command was successful\n    if ($returnCode === 0 && isset($output[0])) {\n        return trim($output[0]);\n    }\n\n    // Return false if we couldn't determine the MIME type\n    return false;\n}\n\n// Example usage\n$file = 'example.txt';\n$mimeType = getMimeType($file);\nif ($mimeType !== false) {\n    echo "MIME type of $file: $mimeType";\n} else {\n    echo "Could not determine the MIME type of $file.";\n}