public function renderSomeHTML($isTopOfThePage)\n{\n    // Generate a unique cache key based on the parameter\n    $cacheKey = 'someHTML_' . ($isTopOfThePage ? 'top' : 'bottom');\n\n    // Check if the content is cached\n    if ($cachedContent = $this->cache->load($cacheKey)) {\n        echo $cachedContent;\n        return;\n    }\n\n    // Generate the content\n    $content = '<div>' . ($isTopOfThePage ? 'Top content' : 'Bottom content') . '</div>';\n\n    // Save the content to cache\n    $this->cache->save($content, $cacheKey);\n\n    // Output the content\n    echo $content;\n}