<?php\n// Parent class\nclass Vehicle {\n    public $make;\n    public $model;\n\n    public function __construct($make, $model) {\n        $this->make = $make;\n        $this->model = $model;\n    }\n\n    public function display() {\n        echo "This vehicle is a " . $this->make . " " . $this->model . ".";\n    }\n}\n\n// Child class inheriting from Vehicle\nclass Car extends Vehicle {\n    public $color;\n\n    public function __construct($make, $model, $color) {\n        parent::__construct($make, $model); // Call the parent constructor\n        $this->color = $color;\n    }\n\n    public function display() {\n        echo "This car is a " . $this->color . " " . $this->make . " " . $this->model . ".";\n    }\n}\n\n$myCar = new Car("Toyota", "Corolla", "blue");\n$myCar->display(); // Outputs: This car is a blue Toyota Corolla.\n?>