$page = isset($_GET['page']) ? (int)$_GET['page'] : 0;\n$per = 10; // Results per page\n$offset = $page * $per;\n\n// SQL query with indexed column and fetching only required columns\n$sql = "SELECT column1, column2 FROM table WHERE conditions ORDER BY indexed_column LIMIT :limit OFFSET :offset";\n$stmt = $pdo->prepare($sql);\n$stmt->bindValue(':limit', $per + 1, PDO::PARAM_INT);\n$stmt->bindValue(':offset', $offset, PDO::PARAM_INT);\n$stmt->execute();\n$resultset = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nif ($page > 0) echo '<a href="http://example.com/table?page=' . ($page - 1) . '">Previous</a>';\nif (count($resultset) > $per) {\n    echo '<a href="http://example.com/table?page=' . ($page + 1) . '">Next</a>';\n    unset($resultset[$per]); // Remove the extra row\n}\n\n// Display results\nforeach ($resultset as $row) {\n    // Display each row (adjust this part according to your needs)\n    echo '<div>' . htmlspecialchars($row['column1']) . ' - ' . htmlspecialchars($row['column2']) . '</div>';\n}