<?php\n// Define the source and destination directories\n$source = '/path/to/source_directory';\n$destination = '/path/to/destination_directory';\n\n// Construct the command to copy the directory in the background\n$command = "cp -r " . escapeshellarg($source) . " " . escapeshellarg($destination) . " > /dev/null 2>&1 &";\n\n// Execute the command\nexec($command);\n\necho "Copy process started in the background.";\n?>