function deleteOldFiles($dir, $days) {\n  $mydir = opendir($dir);\n  if ($mydir === false) {\n    die("Failed to open directory $dir");\n  }\n\n  while (($file = readdir($mydir)) !== false) {\n    if ($file != "." && $file != "..") {\n      $filePath = $dir . DIRECTORY_SEPARATOR . $file;\n      if (filemtime($filePath) <= time() - ($days * 86400)) {\n        // Uncomment the following line to actually delete the files\n        // unlink($filePath) or die("Failed to delete $filePath<br />");\n        echo filemtime($filePath) . "\n";\n      }\n    }\n  }\n  closedir($mydir);\n}