<?php\n// Output the original request URI\necho $_SERVER['REQUEST_URI'] . "\n";\n\n// Parse the query string manually\nparse_str($_SERVER['QUERY_STRING'], $query);\n\n// Access the value using the original key with a dot\nif (isset($query['x.y'])) {\n    echo $query['x.y'] . "\n";\n}\n\n// For comparison, show the value with an underscore key (which will be empty)\necho isset($_GET['x_y']) ? $_GET['x_y'] : 'Not set' . "\n";\n?>