class PersonController extends AppController {\n    var $name = 'Person';\n    var $uses = array('Person', 'Comment');\n    \n    // Other methods\n\n    function addComment() {\n        if (!empty($this->data)) {\n            if ($this->Comment->save($this->data)) {\n                $this->Session->setFlash('Comment has been saved.');\n            } else {\n                $this->Session->setFlash('Unable to add your comment.');\n            }\n        }\n        $this->redirect(array('action' => 'view', $this->data['Comment']['person_id']));\n    }\n}