<?php\n// Custom handler function for YouTube tags\nfunction handle_youtube($content, $params, $node_object) {\n    $youtube_id = htmlspecialchars($content);\n    return '<object width="425" height="350">\n                <param name="movie" value="http://www.youtube.com/v/' . $youtube_id . '"></param>\n                <embed src="http://www.youtube.com/v/' . $youtube_id . '" type="application/x-shockwave-flash" width="425" height="350"></embed>\n            </object>';\n}\n\n// Create a new bbcode parser\n$bbcode = bbcode_create();\n\n// Add custom YouTube tag\nbbcode_add_element($bbcode, 'youtube', [\n    'type' => BBCODE_TYPE_NOARG,\n    'open_tag' => '',\n    'close_tag' => '',\n    'handler' => 'handle_youtube'\n]);\n\n// Example usage\n$input = '[youtube]w0ffwDYo00Q[/youtube]';\n$output = bbcode_parse($bbcode, $input);\n\n// Display the parsed output\necho $output;\n?>