<?php\nif (isset($_POST["add_single"])) {\n    // Sanitize input\n    $id = filter_input(INPUT_POST, 'id', FILTER_SANITIZE_STRING);\n    $add_single = filter_input(INPUT_POST, 'add_single', FILTER_SANITIZE_NUMBER_INT);\n\n    // Debugging output\n    error_log('Received ID: ' . $id);\n    error_log('Received add_single: ' . $add_single);\n\n    // Save to database\n    save_single_bracelet($id, $add_single);\n\n    echo '<script>alert("Values: ' . $id . ', ' . $add_single . '");</script>';\n}\n?>