// Logger.static.php\n<?php\nclass Logger\n{\n    static public function log($msg)\n    {\n        $bt = debug_backtrace();\n        // Get the caller that isn't part of the Logger class\n        foreach ($bt as $trace) {\n            if (isset($trace['file']) && $trace['file'] !== __FILE__) {\n                echo "$msg (file: " . $trace['file'] . ", line number: " . $trace['line'] . ")\n";\n                break;\n            }\n        }\n    }\n}\n?>